import React, { Component } from "react";

export default class Ingredientlist extends Component{
    state = {
        cart: ["Corn", "Potato"],
    };

    saveInput = (e) => {
        this.setState( {input: e.target.value });
    };

    addMore = () => {
        let { cart, input } = this.state;
        this.state.cart.push(this.state.input);
    };

    render(){
        return(
            <div>
                <input
                type="text"
                onChange={this.saveInput}
                />
                <button onClick={this.addMore}> Add Item </button>
                <ul>
                    {this.state.cart.map((subItems) => {
                        return <li>{subItems}</li>
                    })}
                </ul>
            </div>
        );
    }
}
