import React, { Component } from "react";
import axios from "axios";
export default class Ingredientlist extends Component{
    state = {
        cart: [],
        recipes: [],
    };

    saveInput = (e) => {
        this.setState( {input: e.target.value });
    };

    addMore = () => {
        let { cart, input } = this.state;
        this.state.cart.push(this.state.input);
        console.log(this.state.input+" added")
        console.log(this.state.cart.length)
    };

    submitIngredient = () => {
        //e.preventDefault();
        axios.post('http://localhost:3001/api/post', {
            ingredientName: this.state.cart
        }).then(
            (response) => {this.state.recipes.push(response.data)
            console.log(this.state.recipes)}
        )
    }

    render(){
        return(
            <div>
                <input
                type="text"
                onChange={this.saveInput}
                />
                <button onClick={this.addMore}> Add Item </button>
                {/* {this.state.cart.map((subItems) => {
                    return(
                        <div>
                            <ul>
                                <li>{subItems}</li>
                            </ul>
                        </div>
                    )    
                })} */}
                <button onSubmit = {this.submitIngredient}>Submit</button>
                {this.state.recipes.map((subItems) => {
                    return(
                        <div>
                            <ul>
                                <li>Title: {subItems[0].id}</li>
                            </ul>
                        </div>
                    )    
                })}

            </div>
        );
    }
}
