import "./App.css";
import axios from "axios";
import {useState, useEffect} from "react";

const Search = () => {
    const [ingredientName, setIngredientName] = useState("");
    const [recipesList, setRecipesList] = useState([]);
    /*const submitIngredient = () => {
         Axios.post('http://localhost:3001/api/post', {
            ingredientName: ingredientName
        }).then(() => {
            alert('ingredient sent');
        })
    }*/
    //send data to server
    async function submitIngredient(e){
        e.preventDefault();
        try{

            await axios.post('http://localhost:3001/api/post',
               { ingredientName: ingredientName });
               //console.log(response);
        }catch(err){
            console.error(err);
        }
    }
    //get data from server
    //useEffect(() => {
    let name;
    async function getData(){

        await axios.get('http://localhost:3001/api/get').then((res)=>{
            name = res.data;
            console.log(name)
        })
    }
    //})
    return( 
        <form className = "search_form" onSubmit={submitIngredient}>
            <label>Search Bar</label>
            <input
                type="text"
                placeholder="type your ingredient"
                name="ingredientName"
                onChange={ (e)=> {
                    setIngredientName(e.target.value)
                }}

            />
            <button type="submit">Search</button>
            <h1>{name}</h1>
        </form>
    )
}

export default Search;
