import "./App.css";
import axios from "axios";
import {useState} from "react";


const IngredientList = () => {
    //const [ingredientName, setIngredientName] = useState("");
    const ingredientName = [];
    const [matchedRecipes, setMatchedRecipes] = useState([]);


    const submitIngredient = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:3001/api/post', {
            ingredientName: ingredientName
        }).then(
            (response) => setMatchedRecipes(response.data)
            )
    }


        return( 
            <div className = "ingredient-list">
                <div className = "row-1">
                    <input
                        type="checkbox"
                        id = "chicken"
                        name = "chicken"
                        value = "chicken"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="chicken">Chicken</label>

                    <input
                        type="checkbox"
                        id = "flour"
                        name = "flour"
                        value = "flour"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="flour">Flour</label>

                    <input
                        type="checkbox"
                        id = "barbecue sauce"
                        name = "barbecue sauce"
                        value = "barbecue sauce"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="barbecue sauce">Barbecue sauce</label>

                    <input
                        type="checkbox"
                        id = "brown sugar"
                        name = "brown sugar"
                        value = "brown sugar"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="brown sugar">Brown sugar</label>

                    <input
                        type="checkbox"
                        id = "peanut butter"
                        name = "peanut butter"
                        value = "peanut butter"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="peanut butter">Peanut butter</label>

                    <input
                        type="checkbox"
                        id = "egg"
                        name = "egg"
                        value = "egg"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="egg">Egg</label>

                    <input
                        type="checkbox"
                        id = "fish"
                        name = "fish"
                        value = "fish"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="fish">Fish</label>

                    <input
                        type="checkbox"
                        id = "frozen spinach"
                        name = "frozen spinach"
                        value = "frozen spinach"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="frozen spinach">Frozen spinach</label>
                </div>

                <div className = "row-2">
                <input
                        type="checkbox"
                        id = "oleo"
                        name = "oleo"
                        value = "oleo"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="oleo">Oleo</label>

                    <input
                        type="checkbox"
                        id = "powdered sugar "
                        name = "powdered sugar "
                        value = "powdered sugar "
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="powdered sugar ">powdered sugar </label>

                    <input
                        type="checkbox"
                        id = "buttermilk"
                        name = "buttermilk"
                        value = "buttermilk"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="buttermilk">Buttermilk</label>

                    <input
                        type="checkbox"
                        id = "shortening"
                        name = "shortening"
                        value = "shortening"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="shortening">Shortening</label>

                    <input
                        type="checkbox"
                        id = "water chestnuts"
                        name = "water chestnuts"
                        value = "water chestnuts"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="water chestnuts">Water chestnuts</label>

                    <input
                        type="checkbox"
                        id = "soy sauce"
                        name = "soy sauce"
                        value = "soy sauce"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="soy sauce">Soy sauce</label>

                    <input
                        type="checkbox"
                        id = "bacon"
                        name = "bacon"
                        value = "bacon"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="bacon">Bacon</label>

                    <input
                        type="checkbox"
                        id = "pineapple"
                        name = "pineapple"
                        value = "pineapple"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="pineapple">Pineapple</label>

                </div>

                <div className = "row-3">
                <input
                        type="checkbox"
                        id = "walnuts"
                        name = "walnuts"
                        value = "walnuts"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="walnuts">Walnuts</label>
                    <input
                        type="checkbox"
                        id = "lemon juice"
                        name = "lemon juice"
                        value = "lemon juice"
                        onChange = { (e) => {
                            ingredientName.push(e.target.value)
                        }}
                    />
                    <label htmlFor="lemon juice">Lemon juice</label>

                </div>

                <button type="submit" onClick = {submitIngredient}>Submit</button>
                {matchedRecipes.map((val)=>{
                    return(
                        <div key={val.id}>
                            <ul >
                                <li >Title: {val.title}</li>
                                <li >Ingredient: {val.ingredients}</li>
                                <li >Directions: {val.directions}</li>
                                <li >Ingredient string: {val.ingredients_string}</li>
                            </ul>
                        </div>
                    )

                })}
           </div>


        );


    //})


}

export default IngredientList;
